package ru.bioinf.rosalind.alignment;

/**
 * Проблема №6
 * @see <a href = http://rosalind.info/problems/hamm/> Counting Point Mutations. </a>
 * 
 * @author vveprinsky
 */
public class Hamm {

	
	public static void main(String[] args) {
		String s = "";
		String t = "";
		long distanceHamming = 0;
	
		char[] sDNA = s.toCharArray();
		char[] tDNA = t.toCharArray();
		for (int i = 0; i < sDNA.length; i++) {
			if (sDNA[i]!=tDNA[i]) {
				distanceHamming++;
			}
		}
		System.out.println(distanceHamming);
	}

}
